import logo from '../img/logo.png';
import profileIcon from '../img/profile-icon.png';
import './Header.css';
import React, { useState } from 'react';


const Header = () => {
  const [authPopup, setAuthPopup] = useState(false);

  const toggleAuthPopup = () => {
    setAuthPopup(!authPopup);
  };

  return (
    <header className="header">
      <div className="logo">
        <img src={logo} alt="ReSPonSE Logo" />
        <span>ReSPonSE</span>
      </div>
      <nav>
        <a href="#map">Harta proiecte</a>
        <a href="#list">Listă proiecte</a>
        <a href="#search">Căutare proiect</a>
      </nav>
      <div className="search-container">
        <input type="text" placeholder="Căutare" />
      </div>
      <div className="auth">
        <img src={profileIcon} alt="Profile Icon" onClick={toggleAuthPopup} />
        {authPopup && (
          <div className="auth-popup">
            <form>
              <label htmlFor="email">Adresă e-mail</label>
              <input type="email" id="email" placeholder="Introduceți adresa e-mail" />
              <label htmlFor="password">Parola</label>
              <input type="password" id="password" placeholder="Parola" />
              <div className="remember-me">
                <input type="checkbox" id="remember" />
                <label htmlFor="remember">Nu mă uita</label>
              </div>
              <button type="submit">Autentificare</button>
              <a href="4#">Ați uitat parola?</a>
            </form>
          </div>
        )}
      </div>
    </header>
  );
};

export default Header;
